//        }
//        return (int)res % 1000000007;
        int size = (n*(n+1))/2, idx = 0;
        long ans = 0;
        int[] arr = new int[size];
        for(int i=0; i<n; i++){
            int sum = 0;
            for(int j=i; j<n; j++){
                sum += nums[j];
                arr[idx++] = sum;
            }
        }
        Arrays.sort(arr);
        for(int i=left-1; i<right; i++){
            ans += arr[i];
        }
        return (int) (ans%1000000007);
